#include <bits/stdc++.h>using namespace std;int main() {    int t;    cin >> t;    while (t--) {        string s;        cin >> s;        int n = s.length();        int minLen = INT_MAX;        int last1 = -1, last2 = -1, last3 = -1;                for (int i = 0; i < n; i++) {            if (s[i] == '1') last1 = i;            else if (s[i] == '2') last2 = i;            else if (s[i] == '3') last3 = i;                        if (last1 != -1 && last2 != -1 && last3 != -1) {                int minPos = min({last1, last2, last3});                minLen = min(minLen, i - minPos + 1);            }        }                if (minLen == INT_MAX) cout << 0 << endl;        else cout << minLen << endl;    }    return 0;}