#include <bits/stdc++.h>using namespace std;Â int main() {    ios_base::sync_with_stdio(false);    cin.tie(nullptr);        int t;    cin >> t;        while (t--) {        int n;        cin >> n;        vector<long long> a(n);        for (int i = 0; i < n; i++) {            cin >> a[i];        }                sort(a.begin(), a.end());                long long min_val = a[0];        long long min_diff = LLONG_MAX;                // Find minimum positive difference from min_val        for (int i = 1; i < n; i++) {            if (a[i] > min_val) {                min_diff = min(min_diff, a[i] - min_val);            }        }                // If all numbers are the same (shouldn't happen per constraints)        if (min_diff == LLONG_MAX) {            cout << "0\n";        } else {            // Answer is max of:            // 1. min_val (if we target 0)            // 2. min_diff (if we target min_val)            cout << max(min_val, min_diff) << "\n";        }    }        return 0;}