#include <bits/stdc++.h>using namespace std;int main() {    int n, m;    cin >> n >> m;    vector<int> a(m);    for (int i = 0; i < m; i++) cin >> a[i];        // Maximum money    priority_queue<int> maxHeap(a.begin(), a.end());    int maxMoney = 0;    for (int i = 0; i < n; i++) {        int top = maxHeap.top();        maxHeap.pop();        maxMoney += top;        if (top - 1 > 0) maxHeap.push(top - 1);    }        // Minimum money    priority_queue<int, vector<int>, greater<int>> minHeap(a.begin(), a.end());    int minMoney = 0;    for (int i = 0; i < n; i++) {        int top = minHeap.top();        minHeap.pop();        minMoney += top;        if (top - 1 > 0) minHeap.push(top - 1);    }        cout << maxMoney << " " << minMoney;    return 0;}