#include <bits/stdc++.h>using namespace std;int main() {    int n;    cin >> n;    map<int, int> position;        for (int i = 1; i <= n; i++) {        int x;        cin >> x;        position[x] = i;    }        int m;    cin >> m;    long long vasya = 0, petya = 0;        for (int i = 0; i < m; i++) {        int b;        cin >> b;        int pos = position[b];        vasya += pos;        petya += (n - pos + 1);    }        cout << vasya << " " << petya;    return 0;}