#include <bits/stdc++.h>using namespace std;int main() {    int n;    cin >> n;    int sum = 0;        for (int i = 0; i < n; i++) {        int x;        cin >> x;        sum += x;    }        int count = 0;    // Dima can show 1 to 5 fingers    for (int dima = 1; dima <= 5; dima++) {        int total = sum + dima;        // Check if (total % (n + 1)) != 1        // Because position 1 means Dima loses        if (total % (n + 1) != 1) {            count++;        }    }        cout << count;    return 0;}