#include <bits/stdc++.h>using namespace std;int main() {    int n, t;    cin >> n >> t;    vector<int> a(n);        for (int i = 0; i < n; i++) {        cin >> a[i];    }        int maxBooks = 0;    int left = 0, currentTime = 0;        for (int right = 0; right < n; right++) {        currentTime += a[right];                while (currentTime > t) {            currentTime -= a[left];            left++;        }                maxBooks = max(maxBooks, right - left + 1);    }        cout << maxBooks;    return 0;}