#include <bits/stdc++.h>using namespace std;int main() {    int n;    cin >> n;    vector<int> a(n);    for (int i = 0; i < n; i++) cin >> a[i];    int l = 0, r = n - 1;    int s = 0, d = 0;    bool sereja = true;    while (l <= r) {        int pick = (a[l] > a[r]) ? a[l++] : a[r--];        if (sereja) s += pick;        else d += pick;        sereja = !sereja;    }    cout << s << " " << d;    return 0;}