#include <bits/stdc++.h>using namespace std;int main() {    int n;    cin >> n;    map<string, int> database;        for (int i = 0; i < n; i++) {        string name;        cin >> name;                if (database.find(name) == database.end()) {            cout << "OK" << endl;            database[name] = 1;        } else {            cout << name << database[name] << endl;            database[name]++;        }    }        return 0;}