#include <bits/stdc++.h>using namespace std;int main() {    int n, m;    cin >> n >> m;    queue<pair<int, int>> q; // {candies_needed, child_index}        for (int i = 0; i < n; i++) {        int a;        cin >> a;        q.push({a, i + 1});    }        int lastChild = 0;    while (!q.empty()) {        auto front = q.front();        q.pop();                if (front.first <= m) {            lastChild = front.second;        } else {            q.push({front.first - m, front.second});        }    }        cout << lastChild;    return 0;}